<form method="post" action="<%%ABSURL%%>/client/pickups/<%%id%%>">	<div style="float: left; width: 100%; margin-bottom: 10px;">		<div style="float: left; padding-left: 10px;">			<img src="<%%ABSURL%%>/images/PackageIcon.png" />		</div>		<div style="float: left; padding-left: 10px;">			<div style="font-family: georgia; font-size: 36px; margin-bottom: 10px;">				Family Bag Delivery			</div>			<div style="width: 320px;">			  <b class="spiffy">			  <b class="spiffy1"><b></b></b>			  <b class="spiffy2"><b></b></b>			  <b class="spiffy3"></b>			  <b class="spiffy4"></b>			  <b class="spiffy5"></b></b>			  <div class="spiffyfg-d">				<input type="submit" onClick="page_clean(); doDelivery('<%%id%%>'); return false;" value="Complete Delivery" style="font-weight: bold;" />					<input type="submit" onClick="page_clean(); window.location = '/pantry/client/edit/<%%id%%>'; return false;" value="Edit Client" />				  </div>			  <b class="spiffy">			  <b class="spiffy5"></b>			  <b class="spiffy4"></b>			  <b class="spiffy3"></b>			  <b class="spiffy2"><b></b></b>			  <b class="spiffy1"><b></b></b></b>			</div>		</div>	</div>	<input type="hidden" name="did_submit" value="yes" />	<div style="float: left; width: 40%; padding-right: 30px;">	<table class="client_new" style="width: 100%;">		<tr>			<th colspan="2" style="border: 0px; font-weight: bold; font-size: 16px;">				Client Information			</th>		</tr>		<tr class="row1">			<td>				Bag Type			</td>			<td>				<%%bag%%>			</td>		</tr>		<tr class="row2" style="font-weight: bold;">			<td>				First Name			</td> 			<td>				<%%first_name%%>			</td> 		</tr>		<tr class="row1" style="font-weight: bold;">			<td>				Last Name			</td> 			<td>				<%%last_name%%>			</td> 		</tr>		<tr class="row2">			<td>				Gender			</td> 			<td>				<%%gender%%>			</td> 		</tr>		<tr class="row1">			<td>				Date of Birth			</td> 			<td>				<%%dob%%>			</td> 		</tr>		<tr class="row2">			<td>				Address			</td> 			<td>				<%%address%%>			</td> 		</tr>		<tr class="row1">			<td>				Telephone			</td> 			<td>				<%%telephone%%>			</td> 		</tr>		<tr class="row2">			<td>				Start Date: 			</td> 			<td>				<%%start%%>			</td> 		</tr>		<tr class="row1">			<td>				Comments: 			</td> 			<td>				<%%comments%%>			</td>		</tr>	</table>	<table class="client_new" style="width: 100%;">		<tr>			<th colspan="3" style="border: 0px; font-weight: bold; font-size: 16px;">				Financial Aid			</th>		</tr>		<tr class="row2" style="font-weight: bold;">			<td>				Name			</td> 			<td>				Has			</td> 			<td>				Amount			</td> 		</tr><%%aids%%>	</table>	</div>	<table class="client_new">		<tr>			<th colspan="2" style="border: 0px; font-weight: bold; font-size: 16px;">				Other Family Members			</th>		</tr>		<tr class="row2">			<td colspan="2" id="family_members" style="padding: 0px; padding-top: 2px; background-color: white; border: 0px;"><%%family_members%%>			</td>		</tr>	</table>	<table class="client_new">		<tr>			<th colspan="2" style="border: 0px; font-weight: bold; font-size: 16px;">				Products			</th>		</tr>		<tr class="row2">			<td colspan="2" id="family_members" style="padding: 0px; border: 0px;"><%%products%%>			</td>		</tr>	</table></form>